import React from "react";
import "./App.css";
import UtgiftTable from "./UtgiftTable";
import NavnTable from "./NavnTable";

//import PieChart from "react-minimal-pie-chart";
import {
    ResponsiveContainer,
    PieChart,
    Tooltip,
    Pie,
    Legend,
    Label
} from "recharts";

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { users: [] };
        this.update = this.update.bind(this);
        this.post = this.post.bind(this);
        this.fisk = this.fisk.bind(this);
        this.delete = this.delete.bind(this);
    }

    componentDidMount() {
        this.update();
    }
    async update() {
        const response = await fetch("/utgiftlist")
            .then(res => res.json())
            .then(users => this.setState({ users }));
    }

    async post() {
        let txt1 = document.getElementById("txt1").value;
        let txt2 = document.getElementById("txt2").value;
        let txt3 = document.getElementById("txt3").value;
        await fetch("http://localhost:8080/utgiftlist", {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
            },

            body: JSON.stringify([{ navn: txt1, pris: txt2, kategori: txt3 }])
        });

        this.update();
    }

    async delete() {
        await fetch("http://localhost:8080/utgiftlist/0", {
            method: "DELETE",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
            }
            // body: JSON.stringify([{ pris: 2000 }])
        })
            .then(res => res.json())
            .then(user =>
                this.setState(prevState => ({
                    users: prevState.users.filter(el => el != user)
                }))
            );
        this.update();
    }

    fisk() {
        return <label>fisk</label>;
    }

    render() {
        return (
            <div className="App">
                <h1> Utgifter</h1>
                <UtgiftTable users={this.state.users} />
                <NavnTable utgifter={this.state.users} />
                <label>Navn: </label>
                <input type="text" id="txt1" />
                <label>Pris: </label>
                <input type="text" id="txt2" />
                <label>Kategori: </label>
                <input type="text" id="txt3" />
                <button onClick={this.post}>Post</button>
                <button onClick={this.delete}>Delete</button>
            </div>
        );
    }
}

