import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Numbers from "./Numbers";
import Client from "./Client";
import { arrowFunctionExpression } from "@babel/types";
import BtnPost from "./BtnPost";
import UtgiftTable from "./UtgiftTable";
import post from "./BtnPost"

import { ResponsiveContainer, PieChart, Pie } from "recharts";
import { Panel } from "react-bootstrap";

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { users: [], loser: true};
        this.update = this.update.bind(this);
        this.post = this.post.bind(this);
        this.fisk = this.fisk.bind(this);
    }

    componentDidMount() {
        this.update();
        
    }
    async update() {
        const response = await fetch("/utgiftlist")
            .then(res => res.json())
            .then(users => this.setState({ users }));

    }

   

    async post () {
        let txt1 = document.getElementById("txt1").value;
        let txt2 = document.getElementById("txt2").value;
        let txt3 = document.getElementById("txt3").value;
        await fetch("http://localhost:8080/utgiftlist", {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
            },
            mode: "cors",
            body: JSON.stringify([{ navn: txt1, pris: txt2, kategori: txt3 }])
        });
        this.update();

    }
    fisk(){
        return <label>fitte</label>
    }
    data = [{ name: "network", value: 200 }, { name: "network 3", value: 4 }];

    render() {
        return (
            <div className="App">
                <h1> Utgifter</h1>
                <UtgiftTable users={this.state.users} />
               
                <label for ="navn">Navn: </label>
                <input type="text" id="txt1" />
                <label for ="pris">Pris: </label>
                <input type="text" id="txt2" />
                <label for="kategori">Kategori: </label>
                <input type="text" id="txt3" />
                <button onClick={this.post}>Post</button>
                <ResponsiveContainer width="100%" height={250}>
    <PieChart height={250}>
      <Pie
        data={data}
        cx="50%"
        cy="50%"
        outerRadius={100}
        fill="#8884d8"
        dataKey="value"
        label={({
          cx,
          cy,
          midAngle,
          innerRadius,
          outerRadius,
          value,
          index
        }) => {
          console.log("handling label?");
          const RADIAN = Math.PI / 180;
          // eslint-disable-next-line
          const radius = 25 + innerRadius + (outerRadius - innerRadius);
          // eslint-disable-next-line
          const x = cx + radius * Math.cos(-midAngle * RADIAN);
          // eslint-disable-next-line
          const y = cy + radius * Math.sin(-midAngle * RADIAN);

          return (
            <text
              x={x}
              y={y}
              fill="#8884d8"
              textAnchor={x > cx ? "start" : "end"}
              dominantBaseline="central"
            >
              {data[index].name}{": "}{value}{",-"}
            </text>
          );
        }}
      />
    </PieChart>
  </ResponsiveContainer>;
            </div>
        );
    }
}


